\n	white-space: nowrap;\r\n}\r\n\r\ninput[type=\"checkbox\"] {\r\n	transform: scale(1.5);\r\n	margin-right: 0.6em;\r\n	vertical-align: middle;\r\n}\r\n\r\np {\r\n	margin-top: 0.5em;\r\n	margin-bottom: 0.5em;\r\n}\r\n\r\n.image {\r\n	border: none;\r\n	margin: 1.5em 0;\r\n	padding: 0;\r\n	border-radius: 0;\r\n	text-align: center;\r\n}\r\n\r\n.code,\r\ncode {\r\n	background: rgba(135, 131, 120, 0.15);\r\n	border-radius: 3px;\r\n	padding: 0.2em 0.4em;\r\n	border-radius: 3px;\r\n	font-size: 85%;\r\n	tab-size: 2;\r\n}\r\n\r\ncode {\r\n	color: #eb5757;\r\n}\r\n\r\n.code {\r\n	padding: 1.5em 1em;\r\n}\r\n\r\n.code-wrap {\r\n	white-space: pre-wrap;\r\n	word-break: break-all;\r\n}\r\n\r\n.code > code {\r\n	background: none;\r\n	padding: 0;\r\n	font-size: 100%;\r\n	color: inherit;\r\n}\r\n\r\nblockquote {\r\n	font-size: 1.25em;\r\n	margin: 1em 0;\r\n	padding-left: 1em;\r\n	border-left: 3px solid rgb(55, 53, 47);\r\n}\r\n\r\n.bookmark {\r\n	text-decoration: none;\r\n	max-height: 8em;\r\n	padding: 0;\r\n	display: flex;\r\n	width: 100%;\r\n	align-items: stretch;\r\n}\r\n\r\n.bookmark-title {\r\n	font-size: 0.85em;\r\n	overflow: hidden;\r\n	text-overflow: ellipsis;\r\n	height: 1.75em;\r\n	white-space: nowrap;\r\n}\r\n\r\n.bookmark-text {\r\n	display: flex;\r\n	flex-direction: column;\r\n}\r\n\r\n.bookmark-info {\r\n	flex: 4 1 180px;\r\n	padding: 12px 14px 14px;\r\n	display: flex;\r\n	flex-direction: column;\r\n	justify-content: space-between;\r\n}\r\n\r\n.bookmark-image {\r\n	width: 33%;\r\n	flex: 1 1 180px;\r\n	display: block;\r\n	position: relative;\r\n	object-fit: cover;\r\n	border-radius: 1px;\r\n}\r\n\r\n.bookmark-description {\r\n	color: rgba(55, 53, 47, 0.6);\r\n	font-size: 0.75em;\r\n	overflow: hidden;\r\n	max-height: 4.5em;\r\n	word-break: break-word;\r\n}\r\n\r\n.bookmark-href {\r\n	font-size: 0.75em;\r\n	margin-top: 0.25em;\r\n}\r\n\r\n.sans { font-family: ui-sans-serif, -apple-system, BlinkMacSystemFont, \"Segoe UI\", Helvetica, \"Apple Color Emoji\", Arial, sans-serif